import { useState } from 'react';
import './App.css';

function App() {
  const [prompt, setPrompt] = useState('');
  const [imageUrl, setImageUrl] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleGenerate = async () => {
    if (!prompt.trim()) return;

    setLoading(true);
    setImageUrl(null);
    setError(null);

    try {
      // In production, this will be your live Render backend URL
      const response = await fetch('/generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userPrompt: prompt }),
      });

      if (!response.ok) {
        throw new Error('Failed to generate image');
      }

      const data = await response.json();
      setImageUrl(data.imageUrl);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-abyss-black via-gray-900 to-abyss-black flex flex-col items-center justify-center p-4">
      <header className="text-center mb-12 animate-float">
        <h1 className="text-6xl md:text-8xl font-black tracking-wider text-transparent bg-clip-text bg-gradient-to-r from-red-600 to-demon-orange">
          CHRONOS-ABYSS
        </h1>
        <p className="text-gray-400 text-lg mt-2 tracking-widest">THE DEMON FORGE</p>
      </header>

      <main className="w-full max-w-2xl">
        <div className="bg-gray-900 bg-opacity-50 p-6 rounded-lg shadow-2xl border border-gray-800">
          <textarea
            className="w-full p-4 bg-abyss-black border border-gray-700 rounded text-white placeholder-gray-500 focus:outline-none focus:border-demon-orange transition-colors"
            rows="4"
            placeholder="Describe the demon... (e.g., 'a demon with broken wings and a crown of skulls')"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
          />
          <button
            onClick={handleGenerate}
            disabled={loading || !prompt.trim()}
            className={`mt-4 w-full py-4 px-6 font-bold text-lg rounded transition-all duration-300 ${loading || !prompt.trim()
                ? 'bg-gray-700 text-gray-500 cursor-not-allowed'
                : 'bg-gradient-to-r from-red-600 to-demon-orange text-white hover:scale-105 animate-glow'
              }`}
          >
            {loading ? 'FORGING...' : 'GENERATE DEMON'}
          </button>
        </div>

        {error && <p className="text-red-500 text-center mt-4">Error: {error}</p>}

        {loading && (
          <div className="mt-8 text-center">
            <div className="inline-block animate-spin rounded-full h-16 w-16 border-t-4 border-b-4 border-demon-orange"></div>
            <p className="mt-4 text-gray-400">Summoning from the Abyss...</p>
          </div>
        )}

        {imageUrl && (
          <div className="mt-8">
            <h2 className="text-2xl font-bold text-center mb-4 text-gray-300">Your Creation</h2>
            <img
              src={imageUrl}
              alt="Generated demon"
              className="w-full rounded-lg shadow-2xl border border-gray-700"
            />
             <a href={imageUrl} download="demon.png" className="mt-4 block text-center text-demon-orange hover:underline">
              Download Your Masterpiece
            </a>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
